package com.yogeshpaliyal.deepr.ui.components

import androidx.compose.material3.AlertDialog
import androidx.compose.material3.Button
import androidx.compose.material3.Text
import androidx.compose.material3.TextField
import androidx.compose.runtime.Composable
import androidx.compose.runtime.getValue
import androidx.compose.runtime.mutableStateOf
import androidx.compose.runtime.remember
import androidx.compose.runtime.setValue
import com.yogeshpaliyal.deepr.Deepr

@Composable
fun CreateShortcutDialog(deepr: Deepr, onDismiss: () -> Unit, onCreate: (Deepr, String) -> Unit) {
    var shortcutName by remember { mutableStateOf(deepr.link) }
    AlertDialog(
        onDismissRequest = onDismiss,
        title = { Text("Create Shortcut") },
        text = {
            TextField(
                value = shortcutName,
                onValueChange = { shortcutName = it },
                label = { Text("Shortcut Name") }
            )
        },
        confirmButton = {
            Button(onClick = { onCreate(deepr, shortcutName) }) {
                Text("Create")
            }
        },
        dismissButton = {
            Button(onClick = onDismiss) {
                Text("Cancel")
            }
        }
    )
}

package com.yogeshpaliyal.deepr.ui.components

import androidx.compose.material3.AlertDialog
import androidx.compose.material3.Button
import androidx.compose.material3.Text
import androidx.compose.material3.TextField
import androidx.compose.runtime.Composable
import androidx.compose.runtime.getValue
import androidx.compose.runtime.mutableStateOf
import androidx.compose.runtime.remember
import androidx.compose.runtime.setValue
import com.yogeshpaliyal.deepr.Deepr

@Composable
fun CreateShortcutDialog(deepr: Deepr, onDismiss: () -> Unit, onCreate: (Deepr, String) -> Unit) {
    var shortcutName by remember { mutableStateOf("") }
    AlertDialog(
        onDismissRequest = onDismiss,
        title = { Text("Create Shortcut") },
        text = {
            TextField(
                value = shortcutName,
                onValueChange = { shortcutName = it },
                label = { Text("Shortcut Name") },
                placeholder = { Text(text = deepr.link) }
            )
        },
        confirmButton = {
            Button(
                onClick = { onCreate(deepr, shortcutName) },
                enabled = shortcutName.isNotBlank()
            ) {
                Text("Create")
            }
        },
        dismissButton = {
            Button(onClick = onDismiss) {
                Text("Cancel")
            }
        }
    )
}

package com.yogeshpaliyal.deepr.util
import android.content.Context import android.content.Intent import androidx.core.content.pm.ShortcutInfoCompat import androidx.core.content.pm.ShortcutManagerCompat import androidx.core.graphics.drawable.IconCompat import androidx.core.net.toUri import com.yogeshpaliyal.deepr.Deepr import com.yogeshpaliyal.deepr.R
fun createShortcut(context: Context, deepr: Deepr) { if (ShortcutManagerCompat.isRequestPinShortcutSupported(context)) { val shortcutInfo = ShortcutInfoCompat.Builder(context, "deepr_${deepr.id}") .setShortLabel(deepr.link) .setLongLabel(deepr.link) .setIcon(IconCompat.createWithResource(context, R.mipmap.ic_launcher)) .setIntent(Intent(Intent.ACTION_VIEW, deepr.link.toUri()).apply {
}) .build()
ShortcutManagerCompat.requestPinShortcut(context, shortcutInfo, null) } }

package com.yogeshpaliyal.deepr.util
import android.content.Context import android.content.Intent import androidx.core.content.pm.ShortcutInfoCompat import androidx.core.content.pm.ShortcutManagerCompat import androidx.core.graphics.drawable.IconCompat import androidx.core.net.toUri import com.yogeshpaliyal.deepr.Deepr import com.yogeshpaliyal.deepr.R
fun createShortcut(context: Context, deepr: Deepr, shortcutName: String) { if (ShortcutManagerCompat.isRequestPinShortcutSupported(context)) { val shortcutInfo = ShortcutInfoCompat.Builder(context, "deepr_${deepr.id}") .setShortLabel(shortcutName) .setLongLabel(shortcutName) .setIcon(IconCompat.createWithResource(context, R.mipmap.ic_launcher)) .setIntent(Intent(Intent.ACTION_VIEW, deepr.link.toUri()).apply {
}) .build()
ShortcutManagerCompat.requestPinShortcut(context, shortcutInfo, null) } }

